mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, image = #{image}, update_time = #{updateTime}, status = #{status}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select s.*, c.name as categoryName, sd.id as setmealDishId, sd.dish_id as dishId, sd.name as dishName, sd.setmeal_id as setmealId, sd.price as dishPrice, sd.copies as dishCopies
from setmeal s left join category c on s.category_id = c.id left join setmeal_dish sd on s.id = sd.setmeal_id where s.id in
#{id}
